mapper.select:
SELECT EXISTS(
  SELECT 1 FROM users
  WHERE UPPER(email_address) = UPPER(#{emailAddress})

      AND id != #{id}

)

mapper.update:
UPDATE users
SET
    name = #{name}
    , email_address = #{emailAddress}
    , local_authority_id = #{localAuthorityId}
    , role_id = #{roleId}
WHERE id = #{id}

UPDATE users
SET
password = #{password}
WHERE id = #{id}
